                                step ([$class: 'ArtifactArchiver', artifacts: artifacts_selector]);
                            }

                            copyArtifacts filter: 'sapmachine-*.msi', projectName: 'build-19-release-windows_x86_64', target: env.WINDOWS_X86_64_ARTIFACT_DIR, selector:
