definition.script:
pipeline {
    agent {
        label "linux_x86_64"
    }
    stages {
        stage("Checkout Infrastructure Repository") {
            agent {
                dockerfile {
                    dir "SapMachine-Infrastructure/dockerfiles/ubuntu_18_04/x86_64"
                    reuseNode true
                    label "linux_x86_64"
                    additionalBuildArgs '--build-arg ARTIFACTORY_CREDS=\"$ARTIFACTORY_CREDS\"'
                }
            }
            steps {
                sh '''#!/bin/bash
                    set -ex
                    git init SapMachine-Infrastructure && cd SapMachine-Infrastructure
                    git fetch --depth 1 https://github.com/SAP/SapMachine-infrastructure.git master
                    git checkout --detach FETCH_HEAD
                '''
            }
        }

        stage('Example Build') {
            agent {
                dockerfile {
                    dir "SapMachine-Infrastructure/dockerfiles/ubuntu_18_04/x86_64"
                    reuseNode true
                    label "linux_x86_64"
                    additionalBuildArgs '--build-arg ARTIFACTORY_CREDS=\"$ARTIFACTORY_CREDS\"'
                }
            }

            steps {
                echo 'Hello, Maven'
                sh 'mvn --version'
                sh 'uname -a'
            }
        }
        stage('Example Test') {
            agent {
                dockerfile {
                    dir "SapMachine-Infrastructure/dockerfiles/ubuntu_18_04/x86_64"
                    reuseNode true
                    label "linux_x86_64"
                    additionalBuildArgs '--build-arg ARTIFACTORY_CREDS=\"$ARTIFACTORY_CREDS\"'
                }
            }
            steps {
                echo 'Hello, JDK'
                sh 'java -version'
            }
        }
    }
}
